import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import "./Home.css";

function Home() {
  const [kategorije, setKategorije] = useState([]);

  useEffect(() => {
    fetch("http://192.168.1.10:5000/api/categories")
      .then((res) => res.json())
      .then((data) => setKategorije(data))
      .catch((err) =>
        console.error("Greška pri učitavanju kategorija:", err)
      );
  }, []);

  const containerVariants = {
    hidden: {},
    visible: {
      transition: {
        staggerChildren: 0.15, // koliko da čeka između kartica
      },
    },
  };

  const cardVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: { opacity: 1, y: 0 },
  };

  return (
    <div
      className="home-container"
      style={{
        backgroundImage: 'url("/images/pozadina.jpg")',
        backgroundSize: 'cover',
        backgroundPosition: 'top center',
        backgroundRepeat: 'no-repeat',
      }}
    >
      <header className="header">
        <motion.img
          src="/images/logoRestoran1.png"
          alt="Logo"
          className="logo"
          initial={{ opacity: 0, y: -20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.6, ease: "easeOut" }}
        />
      </header>

      <motion.div
        className="kategorije-grid"
        variants={containerVariants}
        initial="hidden"
        animate="visible"
      >
        {kategorije.map((kat, index) => (
          <motion.div
            className="kategorija-card"
            key={index}
            variants={cardVariants}
            transition={{ duration: 0.4, ease: "easeOut" }}
          >
            <motion.div
              className="slika"
              style={{ backgroundImage: `url(${kat.slika})` }}
              whileHover={{ scale: 1.12 }}
              whileTap={{ scale: 1.05 }} // ovo je za dodir na mobilnom
              transition={{ duration: 0.2, ease: "easeOut" }}
            />
            <p>{kat.naziv}</p>
          </motion.div>
        ))}
      </motion.div>
    </div>
  );
}

export default Home;
