// src/themes.js
export const themes = {
  etno: {
    name: "Etno",
    backgroundImage: '/images/pozadina-etno.jpg',
    font: "'Quicksand', sans-serif",
    textColor: '#f3e4c3',
    borderColor: '#c7a86c',
    glowColor: 'rgba(242, 210, 141, 0.7)',
  },
  caffe: {
    name: "Caffe",
    backgroundColor: '#4e3620', // leather saddle brown
    font: "'Raleway', sans-serif",
    textColor: '#4e342e',
    borderColor: '#a1887f',
    glowColor: 'rgba(210, 180, 140, 0.5)',
  },
  salon: {
  name: "Salon",
  backgroundColor: '#fce4ec', // tissue pink nijansa
  font: "'Poppins', sans-serif",
  textColor: '#d81b60',
  borderColor: '#f48fb1',
  glowColor: 'rgba(248, 187, 208, 0.6)',
}
,
};
