import React, { useEffect, useState } from "react";
import { motion } from "framer-motion";
import "./Home.css";

function Home() {
  const [kategorije, setKategorije] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/categories")
      .then((res) => res.json())
      .then((data) => setKategorije(data))
      .catch((err) =>
        console.error("Greška pri učitavanju kategorija:", err)
      );
  }, []);

  return (
    <div className="home-container" style={{
    backgroundImage: 'url("/images/pozadina.jpg")',
    backgroundSize: 'cover',
    backgroundPosition: 'top center',
    backgroundRepeat: 'no-repeat',
  }}>
        
      <header className="header">
      <motion.img
  src="/images/logoRestoran1.png"
  alt="Logo"
  className="logo"
  initial={{ opacity: 0, y: -20 }}
  animate={{ opacity: 1, y: 0 }}
  transition={{ duration: 0.6, ease: "easeOut" }}
/>

      </header>

      <div className="kategorije-grid">
        {kategorije.map((kat, index) => (
<motion.div
  className="kategorija-card"
  key={index}
  variants={{
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 },
  }}
  transition={{ duration: 0.4, ease: "easeOut" }}
>

  <motion.div
    className="slika"
    style={{ backgroundImage: `url(${kat.slika})` }}
    whileHover={{ scale: 1.12 }}
    transition={{ duration: 0.2, ease: "easeOut" }}
  />
  <p>{kat.naziv}</p>
</motion.div>

        ))}
      </div>
    </div>
  );
}

export default Home;
