import React from 'react';
import './Home.css';

useEffect(() => {
  fetch("http://localhost:5000/api/categories")
    .then(res => res.json())
    .then(data => setKategorije(data));
}, []);


function Home() {
  return (
    <div className="home-container">
      <header className="header">
        <h1 className="logo">Moj Restoran</h1>
      </header>

      <div className="kategorije-grid">
        {kategorije.map((kat, index) => (
          <div className="kategorija-card" key={index}>
            <div
              className="slika"
              style={{ backgroundImage: `url(${kat.slika})` }}
            ></div>
            <p>{kat.naziv}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
