import React, { useState, useEffect } from "react";
import "./admin.css";

function Kategorije() {
  const [kategorije, setKategorije] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [novaKategorija, setNovaKategorija] = useState({ naziv: "", slika: "" });

  useEffect(() => {
    fetch("http://localhost:5000/api/categories")
      .then((res) => res.json())
      .then((data) => setKategorije(data));
  }, []);

  const handleInputChange = (e) => {
    setNovaKategorija({ ...novaKategorija, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    fetch("http://localhost:5000/api/categories", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(novaKategorija),
    })
      .then((res) => res.json())
      .then((data) => {
        setKategorije([...kategorije, data]);
        setShowModal(false);
        setNovaKategorija({ naziv: "", slika: "" });
      })
      .catch((err) => console.error("Greška:", err));
  };

  return (
    <div>
      <h2>Kategorije</h2>
      <button className="admin-add-btn" onClick={() => setShowModal(true)}>
        + Dodaj kategoriju
      </button>

      <table className="admin-table">
        <thead>
          <tr>
            <th>Naziv</th>
            <th>Slika</th>
            <th>Akcije</th>
          </tr>
        </thead>
        <tbody>
          {kategorije.map((kat) => (
            <tr key={kat._id}>
              <td>{kat.naziv}</td>
              <td><img src={kat.slika} alt={kat.naziv} width="60" /></td>
              <td>
                <button className="admin-edit">Izmeni</button>
                <button className="admin-delete">Obriši</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>

      {showModal && (
        <div className="admin-modal-overlay">
          <div className="admin-modal">
            <h3>Dodaj novu kategoriju</h3>
            <form onSubmit={handleSubmit}>
              <input
                type="text"
                name="naziv"
                placeholder="Naziv kategorije"
                value={novaKategorija.naziv}
                onChange={handleInputChange}
                required
              />
              <input
                type="text"
                name="slika"
                placeholder="URL slike"
                value={novaKategorija.slika}
                onChange={handleInputChange}
                required
              />
              <div className="admin-modal-actions">
                <button type="submit">Sačuvaj</button>
                <button type="button" onClick={() => setShowModal(false)}>Otkaži</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default Kategorije;
