import React, { useState, useEffect } from "react";
import "./admin.css";

function Kategorije() {
  const [kategorije, setKategorije] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [novaKategorija, setNovaKategorija] = useState({ naziv: "" });
  const [slikaFile, setSlikaFile] = useState(null);
  const [kategorijaZaBrisanje, setKategorijaZaBrisanje] = useState(null);

  useEffect(() => {
    fetch("http://localhost:5000/api/categories")
      .then((res) => res.json())
      .then((data) => {
        console.log("Kategorije sa slikama:", data); // 游녣 Dodaj ovde
        setKategorije(data);
      });
  }, []);

  const handleInputChange = (e) => {
    setNovaKategorija({ ...novaKategorija, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    const formData = new FormData();
    formData.append("naziv", novaKategorija.naziv);
    if (slikaFile) {
      formData.append("slika", slikaFile);
    }

    fetch("http://localhost:5000/api/categories", {
      method: "POST",
      body: formData,
    })
      .then((res) => res.json())
      .then((data) => {
        console.log("九덢잺 Backend vratio:", data);

        // fallback ako slika ili naziv nisu stigli
        const novaKat = {
          ...data,
          naziv: data.naziv || "(bez naziva)",
          slika: data.slika || "",
        };

        setKategorije([...kategorije, novaKat]);
        setShowModal(false);
        setNovaKategorija({ naziv: "" });
        setSlikaFile(null);
      })

      .catch((err) => console.error("Gre코ka:", err));
  };

  return (
    <div>
      <h2>Kategorije</h2>
      <button className="admin-add-btn" onClick={() => setShowModal(true)}>
        + Dodaj kategoriju
      </button>

      <table className="admin-table">
        <thead>
          <tr>
            <th>Naziv</th>
            <th>Slika</th>
            <th>Akcije</th>
          </tr>
        </thead>
        <tbody>
          {kategorije.map((kat) => {
            // Odredi pravilan src za sliku
            let slikaSrc = "";
            if (kat.slika?.startsWith("/uploads/")) {
              slikaSrc = `http://localhost:5000${kat.slika}`;
            } else if (kat.slika?.startsWith("/images/")) {
              slikaSrc = kat.slika; // lokalne slike
            }

            return (
              <tr key={kat._id}>
                <td>{kat.naziv || "(bez naziva)"}</td>
                <td>
                  {slikaSrc ? (
                    <img src={slikaSrc} alt={kat.naziv} width="60" />
                  ) : (
                    <span style={{ color: "#888" }}>Nema slike</span>
                  )}
                </td>
                <td>
                  <button className="admin-edit">Izmeni</button>
                  <button
                    className="admin-delete"
                    onClick={() => handleDelete(kat._id)}
                  >
                    Obri코i
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>

      {showModal && (
        <div className="admin-modal-overlay">
          <div className="admin-modal">
            <h3>Dodaj novu kategoriju</h3>
            <form onSubmit={handleSubmit} encType="multipart/form-data">
              <input
                type="text"
                name="naziv"
                placeholder="Naziv kategorije"
                value={novaKategorija.naziv}
                onChange={handleInputChange}
                required
              />
              <input
                type="file"
                name="slika"
                accept="image/*"
                onChange={(e) => setSlikaFile(e.target.files[0])}
                required
              />
              <div className="admin-modal-actions">
                <button type="submit">Sa캜uvaj</button>
                <button type="button" onClick={() => setShowModal(false)}>
                  Otka쬴
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

const handleDelete = (id) => {
  if (
    !window.confirm("Da li ste sigurni da 쬰lite da obri코ete ovu kategoriju?")
  )
    return;

  fetch(`http://localhost:5000/api/categories/${id}`, {
    method: "DELETE",
  })
    .then((res) => res.json())
    .then(() => {
      setKategorije(kategorije.filter((kat) => kat._id !== id));
    })
    .catch((err) => console.error("Gre코ka pri brisanju:", err));
};

export default Kategorije;
