    glowColor: 'rgba(242, 210, 141, 0.7)',
  },
  caffe: {
    name: "Caffe",
    backgroundImage: '/images/pozadina-caffe.jpg',
    font: "'Raleway', sans-serif",
    textColor: '#4e342e',
    borderColor: '#a1887f',
    glowColor: 'rgba(210, 180, 140, 0.5)',
  },
  salon: {
    name: "Salon",
    backgroundImage: '/images/pozadina-rose.jpg',
