import React, { useEffect, useState } from "react";
import "./admin.css";

function Kategorije() {
  const [kategorije, setKategorije] = useState([]);

  useEffect(() => {
    fetch("http://localhost:5000/api/categories")
      .then(res => res.json())
      .then(data => setKategorije(data))
      .catch(err => console.error("Greška:", err));
  }, []);

  return (
    <div className="admin-page">
      <h2 className="admin-section-title">Kategorije</h2>
      <button className="admin-add-button">+ Dodaj kategoriju</button>
      <table className="admin-table">
        <thead>
          <tr>
            <th>Naziv</th>
            <th>Slika</th>
            <th>Akcije</th>
          </tr>
        </thead>
        <tbody>
          {kategorije.map((kat, index) => (
            <tr key={index}>
              <td>{kat.naziv}</td>
              <td>
                <img src={kat.slika} alt={kat.naziv} className="admin-thumb" />
              </td>
              <td>
                <button className="admin-edit">Izmeni</button>
                <button className="admin-delete">Obriši</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Kategorije;
