import React from 'react';
import './Home.css';

const kategorije = [
  { naziv: 'Pice', slika: '/images/pice.jpg' },
  { naziv: 'Glavna jela', slika: '/images/jela.jpg' },
  { naziv: 'Deserti', slika: '/images/deserti.jpg' },
  { naziv: 'Pića', slika: '/images/pica.jpg' },
  { naziv: 'Kafe', slika: '/images/kafe.jpg' },
];

function Home() {
  return (
    <div className="home-container">
      <header className="header">
        <h1 className="logo">Moj Restoran</h1>
      </header>

      <div className="kategorije-grid">
        {kategorije.map((kat, index) => (
          <div className="kategorija-card" key={index}>
            <div
              className="slika"
              style={{ backgroundImage: `url(${kat.slika})` }}
            ></div>
            <p>{kat.naziv}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Home;
