import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { motion } from "framer-motion";
import { FaArrowUp } from "react-icons/fa";
import "./Proizvodi.css";

function Proizvodi() {
  const { id } = useParams(); // kategorijaId iz URL-a
  const [proizvodi, setProizvodi] = useState([]);
  const [showScrollBtn, setShowScrollBtn] = useState(false);

  useEffect(() => {
    fetch(`${process.env.REACT_APP_BACKEND_URL}/api/products?kat=${id}`)
      .then((res) => res.json())
      .then((data) => setProizvodi(data))
      .catch((err) => console.error("Greška:", err));

    const handleScroll = () => {
      setShowScrollBtn(window.scrollY > 300);
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, [id]);

  const scrollToTop = () => window.scrollTo({ top: 0, behavior: "smooth" });

  return (
    <div className="proizvodi-strana">
      {proizvodi.map((p) => (
        <motion.div
          className="proizvod"
          key={p._id}
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.4 }}
        >
          <img
            src={`${process.env.REACT_APP_BACKEND_URL}${p.slika}`}
            alt={p.naziv}
          />
          <div>
            <h3>{p.naziv}</h3>
            <p>{p.opis}</p>
            <strong>{p.cena.toFixed(2)} RSD</strong>
          </div>
        </motion.div>
      ))}

      {showScrollBtn && (
        <button className="scroll-to-top" onClick={scrollToTop}>
          <FaArrowUp />
        </button>
      )}
    </div>
  );
}

export default Proizvodi;
