// src/admin/AdminLayout.js
import React, { useState } from "react";
import { Link, Outlet } from "react-router-dom";
import "./admin.css";

const AdminLayout = () => {
  const [menuOpen, setMenuOpen] = useState(false);

  return (
    <div className="admin-container">
      <header className="admin-header">
        <button className="menu-toggle" onClick={() => setMenuOpen(!menuOpen)}>
          â˜°
        </button>
        <h1>Admin Panel</h1>
      </header>

      <div className={`admin-sidebar ${menuOpen ? "open" : ""}`}>
        <nav>
          <Link to="/admin/kategorije">Kategorije</Link>
          <Link to="/admin/proizvodi">Proizvodi</Link>
          <Link to="/admin/logo">Logo</Link>
          <Link to="/admin/kontakt">Kontakt</Link>
        </nav>
      </div>

      <main className="admin-content">
        <Outlet />
      </main>
    </div>
  );
};

export default AdminLayout;
